import React, { forwardRef, useImperativeHandle } from 'react'

const pop = forwardRef(({children}, ref) => {
    const [Open, setOpen] = useState(false);
    useImperativeHandle(ref, () => {
        return{
            open : () => setOpen(true)
        }
    })
})
